import React, { useState } from "react";

function Login({ onLoginClick,onSwitchToSignup }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email || !password) {
      alert("Please enter email and password");
      return;
    }
    alert(`Logging in with:\nEmail: ${email}\nPassword: ${password}`);
    // For now, just simulate login success by calling prop function
    if (onLoginClick) onLoginClick(email);
  };

  return (
    <div style={{ maxWidth: 400, margin: "50px auto", fontFamily: "Arial", padding: 20 }}>
      <h2 style={{ textAlign: "center" }}>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ width: "100%", padding: 8, marginBottom: 12 }}
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ width: "100%", padding: 8, marginBottom: 20 }}
        />
        <button type="submit" style={{ width: "100%", padding: 10, backgroundColor: "#007BFF", color: "white", border: "none", cursor: "pointer" }}>
          Login
        </button>
      </form>
      <p style={{ textAlign: "center", marginTop: 15 }}>
        Donâ€™t have an account? <button
  onClick={onSwitchToSignup}
  style={{ color: "#007BFF", background: "none", border: "none", cursor: "pointer" }}
>
  Signup
</button>

      </p>
    </div>
  );
}

export default Login;
