import React, { useState } from "react";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Profile from "./pages/Profile";
import Recommendations from "./pages/Recommendations";
import Navbar from "./components/Navbar";

function App() {
  const [userEmail, setUserEmail] = useState(null);
  const [showLogin, setShowLogin] = useState(true);
  const [profileCompleted, setProfileCompleted] = useState(false);
  const [showRecommendations, setShowRecommendations] = useState(false);

  if (userEmail && showRecommendations) {
    return (
      <>
        <Navbar />
        <Recommendations userEmail={userEmail} />
      </>
    );
  }

  if (userEmail && profileCompleted) {
    return (
      <>
        <Navbar />
        <div style={{ marginTop: 60, padding: 20 }}>
          <h1>Welcome, {userEmail}!</h1>
          <button
            onClick={() => setShowRecommendations(true)}
            style={{ padding: 10, margin: "20px" }}
          >
            View Internship Recommendations
          </button>
        </div>
      </>
    );
  }

  if (userEmail && !profileCompleted) {
    return (
      <>
        <Navbar />
        <Profile userEmail={userEmail} onProfileSaved={() => setProfileCompleted(true)} />
      </>
    );
  }

  return (
    <>
      <Navbar />
      {showLogin ? (
        <Login onLoginClick={setUserEmail} onSwitchToSignup={() => setShowLogin(false)} />
      ) : (
        <Signup onSignupClick={setUserEmail} onSwitchToLogin={() => setShowLogin(true)} />
      )}
    </>
  );
}

export default App;
