import React, { useState } from "react";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Profile from "./pages/Profile";

function App() {
  const [userEmail, setUserEmail] = useState(null);
  const [showLogin, setShowLogin] = useState(true);
  const [profileCompleted, setProfileCompleted] = useState(false);

  if (userEmail && profileCompleted) {
    return <h1>Welcome, {userEmail}! Here are your personalized internship recommendations.</h1>;
  }

  if (userEmail && !profileCompleted) {
    return <Profile userEmail={userEmail} onProfileSaved={() => setProfileCompleted(true)} />;
  }

  return showLogin ? (
    <Login onLoginClick={setUserEmail} onSwitchToSignup={() => setShowLogin(false)} />
  ) : (
    <Signup onSignupClick={setUserEmail} onSwitchToLogin={() => setShowLogin(true)} />
  );
}

export default App;
