import React, { useState } from "react";

function App() {
  const [education, setEducation] = useState("");
  const [degree, setDegree] = useState("");
  const [sector, setSector] = useState("");
  const [location, setLocation] = useState("");
  const [skills, setSkills] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Submitted Details:
      Education: ${education}
      Degree: ${degree}
      Sector: ${sector}
      Location: ${location}
      Skills: ${skills}`);
  };

  return (
    <div style={{ maxWidth: 400, margin: "50px auto", fontFamily: "Arial", padding: 20 }}>
      <h1 style={{ textAlign: "center" }}>PM Internship Recommendation</h1>
      <p style={{ textAlign: "center" }}>
        Enter your details to get personalized internship suggestions.
      </p>
      <form onSubmit={handleSubmit}>
        <label>Education Level:</label>
        <select value={education} onChange={(e) => setEducation(e.target.value)} required style={{ width: "100%", padding: 8, marginBottom: 12 }}>
          <option value="">Select</option>
          <option value="High School">High School</option>
          <option value="Undergraduate">Undergraduate</option>
          <option value="Postgraduate">Postgraduate</option>
        </select>

        <label>Degree/Discipline:</label>
        <input type="text" value={degree} onChange={(e) => setDegree(e.target.value)} placeholder="e.g. Computer Science" required style={{ width: "100%", padding: 8, marginBottom: 12 }} />

        <label>Sector of Interest:</label>
        <select value={sector} onChange={(e) => setSector(e.target.value)} required style={{ width: "100%", padding: 8, marginBottom: 12 }}>
          <option value="">Select</option>
          <option value="IT/Software">IT/Software</option>
          <option value="Healthcare">Healthcare</option>
          <option value="Agriculture">Agriculture</option>
          <option value="Education">Education</option>
          <option value="Environmental">Environmental</option>
        </select>

        <label>Preferred Location:</label>
        <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} placeholder="City or Region" required style={{ width: "100%", padding: 8, marginBottom: 12 }} />

        <label>Key Skills (comma separated):</label>
        <input type="text" value={skills} onChange={(e) => setSkills(e.target.value)} placeholder="e.g. Python, Excel" required style={{ width: "100%", padding: 8, marginBottom: 20 }} />

        <button type="submit" style={{ width: "100%", padding: 10, backgroundColor: "#007BFF", color: "white", border: "none", cursor: "pointer" }}>
          Get Recommendations
        </button>
      </form>
    </div>
  );
}

export default App;
