import React, { useState } from "react";

function Profile({ userEmail, onProfileSaved }) {
  const [education, setEducation] = useState("");
  const [degree, setDegree] = useState("");
  const [sector, setSector] = useState("");
  const [location, setLocation] = useState("");
  const [skills, setSkills] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!education || !degree || !sector || !location || !skills) {
      alert("Please fill all fields");
      return;
    }

    const profileData = {
      student_id: Date.now(), // generate unique ID; replace with server-generated ID if needed
      name: userEmail,        // replace with name input if available
      email: userEmail,
      qualification: degree,
      skills: skills.split(",").map((skill) => skill.trim()),
      location_pref: location,
      rural_background: false,       // update if you add form input for this
      social_category: "General",    // update if you add form input for this
      past_internships: 0            // update if you add form input for this
    };

    setLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:5000/register_student", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(profileData),
      });

      const data = await response.json();

      if (response.ok) {
        alert(data.message || "Profile saved successfully!");
        if (onProfileSaved) onProfileSaved();
      } else {
        alert("Failed to save profile: " + (data.message || "Unknown error"));
      }
    } catch (error) {
      alert("Network error: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: "50px auto", fontFamily: "Arial", padding: 20 }}>
      <h2 style={{ textAlign: "center" }}>Complete Your Profile</h2>
      <form onSubmit={handleSubmit}>
        <label>Education Level:</label>
        <select
          value={education}
          onChange={(e) => setEducation(e.target.value)}
          required
          style={{ width: "100%", padding: 8, marginBottom: 12 }}
        >
          <option value="">Select</option>
          <option value="High School">High School</option>
          <option value="Undergraduate">Undergraduate</option>
          <option value="Postgraduate">Postgraduate</option>
        </select>

        <label>Degree/Discipline:</label>
        <input
          type="text"
          value={degree}
          onChange={(e) => setDegree(e.target.value)}
          placeholder="e.g. Computer Science"
          required
          style={{ width: "100%", padding: 8, marginBottom: 12 }}
        />

        <label>Sector of Interest:</label>
        <select
          value={sector}
          onChange={(e) => setSector(e.target.value)}
          required
          style={{ width: "100%", padding: 8, marginBottom: 12 }}
        >
          <option value="">Select</option>
          <option value="IT/Software">IT/Software</option>
          <option value="Healthcare">Healthcare</option>
          <option value="Agriculture">Agriculture</option>
          <option value="Education">Education</option>
          <option value="Environmental">Environmental</option>
        </select>

        <label>Preferred Location:</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="City or Region"
          required
          style={{ width: "100%", padding: 8, marginBottom: 12 }}
        />

        <label>Key Skills (comma separated):</label>
        <input
          type="text"
          value={skills}
          onChange={(e) => setSkills(e.target.value)}
          placeholder="e.g. Python, Excel"
          required
          style={{ width: "100%", padding: 8, marginBottom: 20 }}
        />

        <button
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: 10,
            backgroundColor: "#28a745",
            color: "white",
            border: "none",
            cursor: loading ? "not-allowed" : "pointer",
          }}
        >
          {loading ? "Saving..." : "Save Profile"}
        </button>
      </form>
    </div>
  );
}

export default Profile;
